import React, { useState, useEffect, ChangeEvent } from 'react';

const CityInput = () => {
    // Стан для збереження введеного міста
    const [selectedCity, setSelectedCity] = useState('');
    // Стан для збереження списку міст
    const [cities, setCities] = useState<string[]>([]);

    // Завантаження міст з JSON-файлу за допомогою useEffect
    useEffect(() => {
        const fetchCities = async () => {
            try {
                // Замініть 'cities.json' на шлях до вашого JSON-файлу
                const response = await fetch('../city.json');
                const data = await response.json();
                setCities(data);
            } catch (error) {
                console.error('Помилка завантаження міст', error);
            }
        };

        fetchCities();
    }, []);

    // Обробник зміни значення інпуту
    const handleInputChange = (event: ChangeEvent<HTMLSelectElement>) => {
        setSelectedCity(event.target.value);
    };

    return (
        <div>
            <label>Виберіть місто:</label>
            <select value={selectedCity} onChange={handleInputChange}>
                <option value="">Оберіть місто</option>
                {cities.map((city, index) => (
                    <option key={index} value={city}>
                        {city}
                    </option>
                ))}
            </select>
            <p>Вибране місто: {selectedCity}</p>
        </div>
    );
};

export default CityInput;
