import React, { useState }  from 'react';
import { useFormik } from 'formik';
import showImg from '../../img/show.png';
import hideImg from '../../img/hide.png';

const SignUpForm = () => {

    const [showPassword, setShowPassword] = useState(false);

    const formik = useFormik({
        initialValues: {
            userFirstName: '',
            userLastName: '',
            phoneNumber: '',
            email: '',
            password: '',
        },
        onSubmit: values => {
            alert(JSON.stringify(values, null, 2));
        },
    });

    return (
        <form onSubmit={formik.handleSubmit} className='flex flex-col max-w-md mx-auto'>
            <div className="mb-4">
                <label htmlFor="userFirstName" className="text-lg font-semibold">Ім'я</label>
                <input
                    id="userFirstName"
                    name="userFirstName"
                    type="text"
                    onChange={formik.handleChange}
                    value={formik.values.userFirstName}
                    className="border p-2 mt-1 w-full"
                />
            </div>

            <div className="mb-4">
                <label htmlFor="userLastName" className="text-lg font-semibold">Прізвище</label>
                <input
                    id="userLastName"
                    name="userLastName"
                    type="text"
                    onChange={formik.handleChange}
                    value={formik.values.userLastName}
                    className="border p-2 mt-1 w-full"
                />
            </div>

            <div className="mb-4">
                <label htmlFor="phoneNumber" className="text-lg font-semibold">Номер телефону</label>
                <input
                    id="phoneNumber"
                    name="phoneNumber"
                    type="text"
                    onChange={formik.handleChange}
                    value={formik.values.phoneNumber}
                    className="border p-2 mt-1 w-full"
                />
            </div>

            <div className="mb-4">
                <label htmlFor="email" className="text-lg font-semibold">Email</label>
                <input
                    id="email"
                    name="email"
                    type="email"
                    onChange={formik.handleChange}
                    value={formik.values.email}
                    className="border p-2 mt-1 w-full"
                />
            </div>

            <div className="mb-4 relative">
                <label htmlFor="password" className="text-lg font-semibold">Password</label>
                <div className="flex">
                    <input
                        id="password"
                        name="password"
                        type={showPassword ? "text" : "password"}
                        onChange={formik.handleChange}
                        value={formik.values.password}
                        className="border p-2 mt-1 w-full pr-10" // Додано правий відступ для кнопки
                    />
                    <button
                        type="button"
                        onClick={() => setShowPassword(!showPassword)}
                        className="absolute right-1 top-9 p-2  rounded" // Позиціонуємо кнопку вгорі справа
                    >
                        <img
                            src={showPassword ? hideImg : showImg}
                            alt={showPassword ? "Hide" : "Show"}
                            className="h-5 w-5"
                        />
                    </button>
                </div>
            </div>

            <button type="submit" className="bg-blue-500 text-white p-2 rounded">Submit</button>
        </form>
    );
};

export default SignUpForm;
