import React, { useState }  from 'react';
import { useFormik } from 'formik';
import showImg from '../../img/show.png';
import hideImg from '../../img/hide.png';
import classes from "./SignUpForm.module.css";

const SignUpForm = () => {

    const [showPassword, setShowPassword] = useState(false);

    const formik = useFormik({
        initialValues: {
            userFirstName: '',
            userLastName: '',
            phoneNumber: '',
            email: '',
            password: '',
        },
        onSubmit: values => {
            alert(JSON.stringify(values, null, 2));
        },
    });

    return (

        <form onSubmit={formik.handleSubmit} className='flex flex-col max-w-sm	 mx-auto w-370 h-547 bg-gray-100 rounded-xl p-6'>
            <p className="text-3xl font-bold tracking-tight	mb-5">Реєстрація</p>
            <hr className="mb-5 bg-black p-px w-full"/>
            <div className="mb-3">
                <label htmlFor="userFirstName" className="text-sm font-light	tracking-tight text-gray-500">Ім'я</label>
                <input
                    id="userFirstName"
                    name="userFirstName"
                    type="text"
                    onChange={formik.handleChange}
                    value={formik.values.userFirstName}
                    className="border p-2  w-full  rounded-lg "
                />
            </div>

            <div className="mb-3">
                <label htmlFor="userLastName" className="text-sm font-light	tracking-tight text-gray-500">Прізвище</label>
                <input
                    id="userLastName"
                    name="userLastName"
                    type="text"
                    onChange={formik.handleChange}
                    value={formik.values.userLastName}
                    className="border p-2  w-full rounded-lg"
                />
            </div>

            <div className="mb-3">
                <label htmlFor="phoneNumber" className="text-sm font-light	tracking-tight text-gray-500">Номер телефону</label>
                <input
                    id="phoneNumber"
                    name="phoneNumber"
                    type="text"
                    onChange={formik.handleChange}
                    value={formik.values.phoneNumber}
                    className="border p-2  w-full rounded-lg"
                />
            </div>

            <div className="mb-3">
                <label htmlFor="email" className="text-sm font-light	tracking-tight text-gray-500">Ел. почта</label>
                <input
                    id="email"
                    name="email"
                    type="email"
                    onChange={formik.handleChange}
                    value={formik.values.email}
                    className="border p-2  w-full rounded-lg"
                />
            </div>

            <div className="mb-3 relative">
                <label htmlFor="password" className="text-sm font-light	tracking-tight text-gray-500">Придумайте пароль</label>
                <div className="flex">
                    <input
                        id="password"
                        name="password"
                        type={showPassword ? "text" : "password"}
                        onChange={formik.handleChange}
                        value={formik.values.password}
                        className="border p-2  w-full pr-10 rounded-lg"
                    />
                    <button
                        type="button"
                        onClick={() => setShowPassword(!showPassword)}
                        className="absolute right-1 top-6 p-2  rounded"
                    >
                        <img
                            src={showPassword ? hideImg : showImg}
                            alt={showPassword ? "Hide" : "Show"}
                            className="h-7 w-7"
                        />
                    </button>
                </div>
            </div>

            <button type="submit" className="bg-amber-200 rounded-lg p-2 mx-12 my-5">Зареєструватися</button>
            <a href="#" className={classes.a}>Я вже зареєстрований</a>
        </form>

    );
};

export default SignUpForm;
